import { useEffect, useState } from "react";
import Jumbotron from "../ components/cards/Jumbotron";
import axios from "axios";
import ProductCard from "../ components/cards/ProductCard";
import {Checkbox} from "antd";

export default function Shop() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const { data } = await axios.get("/products");
      setProducts(data);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    loadCategories();
  }, []);

  const loadCategories = async () => {
    try {
      const { data } = await axios.get("/categories");
      setCategories(data);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      <Jumbotron title="Hello World" sutTitle="Welcome to React E-commerce" />
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-3">
          <h2 className="p-3 mt-2 mb-2 h4 bg-light text-center">Filter by category</h2>

          </div>
          <div className="col-md-9">
            <h2 className="p-3 mt-2 mb-2 h4 bg-light text-center">{products?.length} Products</h2>
            <div className="row">
              {products?.map((p) => (
                <div className="col-md-4" key={p._id}>
                  <ProductCard p={p} />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
